import {GET_ITEMS, ADD_ITEM, DELETE_ITEM, ITEMS_LOADING} from "../actions/types";

const initialState = {
  items: [],
  loading: false  // In the process of getting data, will be set to true, once it gets back, set to false

};

export default function (state = initialState, action) {
  switch (action.type) {
    case GET_ITEMS:
      return {
        ...state,
        items: action.payload,
        loading: false    // Before we called the request we set loading to true, have to set it back
      };
    case DELETE_ITEM:
      return {
        ...state,
          items: state.items.filter(item => item._id !== action.payload),
      };
    case ADD_ITEM:
      return{
        ...state,
        items: [action.payload, ...state.items],
        loading: false
      }
    case ITEMS_LOADING:
      return{
        ...state,
        loading: true
      }
    default:
      return state;
  }
}
